class TryRandom {
static public void main(String[] sonja) {
pln("TryRandom: Jetzt geht es los!");

Random r = new Random();

for (int i=0; i< 25; i++){
pln(r.nextBoolean());
}
pln("TryRandom: Das war's erstmal!");
} // main

// Eine Methode mit einem kurzen Namen:
static void pln(Object ob) {System.out.println(ob);}

} // class TryRandom


