    /**
     *
     * @param n, a positive integer number
     * @param z, a positive digit from 1 to 9
     */
    public static void met(int n, int z){
        if (n<10){
            if (n==z){
            System.out.print(n);
            }
            System.out.println();
        } else {
            if (n%10 == z) {
                System.out.print(z);
            }
            met(n/10, z);
        }
    }

